const incoming = ($json && ($json.body ?? $json)) ?? ($input && $input.item && $input.item.json && ($input.item.json.body ?? $input.item.json)) ?? {};
const body = incoming;
const nowIso = new Date().toISOString();

const computeUniqueHash = (ipfsURI) => {
  const link = String(ipfsURI || '').trim();
  if (!link) return `hash-${Date.now()}`;
  try {
    const crypto = require('crypto');
    return 'hash-' + crypto.createHash('sha256').update(link).digest('hex');
  } catch (e) {
    let h = 0;
    for (let i = 0; i < link.length; i++) {
      h = ((h << 5) - h) + link.charCodeAt(i);
      h |= 0;
    }
    return 'hash-fallback-' + Math.abs(h).toString(16);
  }
};

const makeDoc = (item = {}) => {
  const tokenId = String(item.tokenId || ('0.0.' + (Math.floor(Math.random() * 9000000) + 1000000)));
  const serialNumber = String(item.serialNumber || '1');
  const universityId = item.universityId || null;
  const studentAccountId = (item.studentAccountId === undefined) ? null : item.studentAccountId;
  const ipfsURI = item.ipfsURI || 'ipfs://QmTest';
  const uniqueHash = item.uniqueHash || computeUniqueHash(ipfsURI);
  const ext = item.externalProofs || {};
  const externalProofs = {
    hederaTx: ext.hederaTx || null,
    xrpTxHash: ext.xrpTxHash || null,
    algoTxId: ext.algoTxId || null
  };
  return {
    tokenId,
    serialNumber,
    universityId,
    studentAccountId,
    uniqueHash,
    ipfsURI,
    externalProofs,
    createdAt: nowIso,
    updatedAt: nowIso,
    __v: 0
  };
};

if (Array.isArray(body)) {
  const docs = body.map(makeDoc);
  return docs.map(d => ({ json: d }));
} else {
  return { json: makeDoc(body) };
}
